func min(a, b int) int {
	if a > b {
		return b
	}
	return a
}

func minCostClimbingStairs(cost []int) int {
	memory := make(map[int]int)

	var inner func([]int, int) int
	inner = func(costs []int, i int) int {
		if val, ok := memory[i]; ok {
			return val
		}
		next := 0
		if i+1 < len(costs) {
			next = inner(costs, i+1)
		}

		nextNext := 0
		if i+2 < len(costs) {
			nextNext = inner(costs, i+2)
		}

		result := min(next, nextNext)
		if i >= 0 {
			result += costs[i]
		}
		memory[i] = result
		return result
	}

	return inner(cost, -1)
}

